import React, { useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import resourceTimelinePlugin from '@fullcalendar/resource-timeline';
import Sidebar from '../../components/Sidebar/Sidebar';
import './Scheduler.css';

const Scheduler = () => {
  const [events, setEvents] = useState([
    {
      id: '1',
      title: 'Flight Training',
      start: '2024-12-15T09:00:00',
      end: '2024-12-15T12:00:00',
      resourceId: 'staff1',
    },
    {
      id: '2',
      title: 'Aircraft Maintenance',
      start: '2024-12-15T13:00:00',
      end: '2024-12-15T15:00:00',
      resourceId: 'airplane1',
    },
  ]);

  const resources = [
    { 
      id: 'instructors-header',
      title: 'Flight Instructors',
      children: [
        { id: 'staff1', title: 'Capt. Sarah Mitchell' },
        { id: 'staff2', title: 'Capt. James Rodriguez' },
        { id: 'staff3', title: 'Capt. Emily Chen' },
        { id: 'staff4', title: 'Capt. Michael O\'Connor' },
        { id: 'staff5', title: 'Capt. David Thompson' },
      ]
    },
    { 
      id: 'aircraft-header',
      title: 'Aircraft Fleet',
      children: [
        { id: 'airplane1', title: 'Cessna 172 (ZK-ABC)' },
        { id: 'airplane2', title: 'Piper Cub (ZK-XYZ)' },
        { id: 'airplane3', title: 'Diamond DA40 (ZK-NGF)' },
        { id: 'airplane4', title: 'Cirrus SR20 (ZK-PQR)' },
        { id: 'airplane5', title: 'Beechcraft B19 (ZK-LMN)' },
      ]
    }
  ];

  const handleDateSelect = (selectInfo) => {
    const title = prompt('Enter booking title:');
    const calendarApi = selectInfo.view.calendar;

    calendarApi.unselect();

    if (title) {
      setEvents((prevEvents) => [
        ...prevEvents,
        {
          id: String(prevEvents.length + 1),
          title,
          start: selectInfo.startStr,
          end: selectInfo.endStr,
          resourceId: selectInfo.resource.id,
        },
      ]);
    }
  };

  const handleEventClick = (clickInfo) => {
    if (window.confirm(`Are you sure you want to delete '${clickInfo.event.title}'?`)) {
      clickInfo.event.remove();
    }
  };

  return (
    <div className="app-container">
      <Sidebar />
      <div className="main-content">
        <div className="calendar-container">
          <div className="header">
            <h1>Schedule</h1>
            <button className="new-booking-btn">+ New Booking</button>
          </div>
          <div className="calendar-wrapper">
            <FullCalendar
              plugins={[resourceTimelinePlugin]}
              initialView="resourceTimelineDay"
              resources={resources}
              events={events}
              select={handleDateSelect}
              eventClick={handleEventClick}
              headerToolbar={{
                left: 'prev,next today',
                center: 'title',
                right: 'resourceTimelineDay',
              }}
              selectable={true}
              resourceAreaWidth="250px"
              height="auto"
              slotDuration="00:30:00"
              resourceGroupField="title"
              resourceAreaHeaderContent="Resources"
              slotMinWidth={100}
              resourcesInitiallyExpanded={true}
              eventOverlap={false}
              slotEventOverlap={false}
              resourceAreaColumns={[
                {
                  field: 'title',
                  headerContent: 'Resources',
                }
              ]}
              dayCellDidMount={(arg) => {
                arg.el.style.borderBottom = '1px solid #e5e7eb';
              }}
              resourceLabelDidMount={(arg) => {
                const trElement = arg.el.closest('tr');
                if (trElement) {
                  trElement.style.borderBottom = '1px solid #e5e7eb';
                }
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Scheduler;
