import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import resourceTimelinePlugin from '@fullcalendar/resource-timeline';
import interactionPlugin from '@fullcalendar/interaction';
import { useAppContext } from '../../context/AppContext';
import './Scheduler.css';
import './ConfirmationDialog.css';

const Scheduler = () => {
  const { staffList, aircraftList, membersList } = useAppContext();
  const [events, setEvents] = useState([
    {
      id: '1',
      title: 'Flight Training',
      start: '2024-12-15T09:00:00',
      end: '2024-12-15T12:00:00',
      resourceId: 'staff1',
    },
    {
      id: '2',
      title: 'Aircraft Maintenance',
      start: '2024-12-15T13:00:00',
      end: '2024-12-15T15:00:00',
      resourceId: 'airplane1',
    },
  ]);

  
  
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [newBooking, setNewBooking] = useState({
    aircraftId: '',
    staffId: '',
    memberId: '',
    date: '',
    startTime: '',
    endTime: '',
    comments: ''
  });

  const resources = [
    { 
      id: 'instructors-header',
      title: 'Flight Instructors',
      children: staffList.map(staff => ({
        id: staff.id,
        title: `Capt. ${staff.firstName} ${staff.lastName}`
      }))
    },
    { 
      id: 'aircraft-header',
      title: 'Aircraft Fleet',
      children: aircraftList.map(aircraft => ({
        id: aircraft.id,
        title: `${aircraft.type} (${aircraft.registration})`
      }))
    }
  ];

  useEffect(() => {
    const rows = document.querySelectorAll('.fc-timeline-body tr');
    rows.forEach(row => {
      row.style.borderBottom = '1px solid #ddd';
    });
  }, []);

  const handleDateSelect = (selectInfo) => {
    const title = prompt('Enter booking title:');
    const calendarApi = selectInfo.view.calendar;

    calendarApi.unselect();

    if (title) {
      setEvents((prevEvents) => [
        ...prevEvents,
        {
          id: String(prevEvents.length + 1),
          title,
          start: selectInfo.startStr,
          end: selectInfo.endStr,
          resourceId: selectInfo.resource.id,
        },
      ]);
    }
  };

  const handleEventClick = (clickInfo) => {
    if (window.confirm(`Are you sure you want to delete '${clickInfo.event.title}'?`)) {
      clickInfo.event.remove();
    }
  };

  const handleEventDrop = (info) => {
    info.revert();

    const dialog = document.createElement('div');
    dialog.className = 'confirmation-dialog';
    dialog.innerHTML = `
      <div class="confirmation-content">
        <h3>Confirm Booking Change</h3>
        <p>Are you sure you want to move this booking?</p>
        <div class="confirmation-buttons">
          <button class="confirm-btn">Yes</button>
          <button class="cancel-btn">No</button>
        </div>
      </div>
    `;

    document.body.appendChild(dialog);

    dialog.querySelector('.confirm-btn').addEventListener('click', () => {
      setEvents(events.map(event => {
        if (event.id === info.event.id) {
          return {
            ...event,
            start: info.event.startStr,
            end: info.event.endStr,
            resourceId: info.event.getResources()[0].id,
          };
        }
        return event;
      }));
      document.body.removeChild(dialog);
    });

    dialog.querySelector('.cancel-btn').addEventListener('click', () => {
      document.body.removeChild(dialog);
    });
  };

  const handleCreateBooking = (e) => {
    e.preventDefault();
    const { date, startTime, endTime, aircraftId, staffId } = newBooking;
    
    const selectedStaff = staffList.find(staff => staff.id === staffId);
    const selectedAircraft = aircraftList.find(aircraft => aircraft.id === aircraftId);
    
    const newEvent = {
      id: String(events.length + 1),
      title: `Flight: ${selectedStaff.firstName} ${selectedStaff.lastName} - ${selectedAircraft.registration}`,
      start: `${date}T${startTime}`,
      end: `${date}T${endTime}`,
      resourceId: staffId,
      aircraftId: aircraftId
    };

    setEvents([...events, newEvent]);
    setShowBookingForm(false);
    setNewBooking({
      aircraftId: '',
      staffId: '',
      memberId: '',
      date: '',
      startTime: '',
      endTime: '',
      comments: ''
    });
  };

  return (
    <div className="main-content">
      <div className="calendar-container">
        <div className="header">
          <h1>Schedule</h1>
          <button 
            className="new-booking-btn"
            onClick={() => setShowBookingForm(true)}
          >
            + New Booking
          </button>
        </div>
        <div className="calendar-wrapper">
          <FullCalendar
            plugins={[resourceTimelinePlugin, interactionPlugin]}
            initialView="resourceTimelineDay"
            resources={resources}
            events={events}
            select={handleDateSelect}
            eventClick={handleEventClick}
            eventDrop={handleEventDrop}
            editable={true}
            headerToolbar={{
              left: 'prev,next today',
              center: 'title',
              right: 'resourceTimelineDay,resourceTimelineWeek',
            }}
            selectable={true}
            resourceAreaWidth="250px"
            height="auto"
            slotDuration="00:30:00"
            resourceGroupField="title"
            dayCellClassNames="timeline-cell"
            resourceLabelClassNames="resource-label"
            resourceAreaColumns={[
              {
                field: 'title',
                headerContent: 'Resources',
              }
            ]}
          />
        </div>
      </div>
      
      {showBookingForm && (
        <div className="modal-overlay">
          <div className="booking-form-modal">
            <div className="modal-header">
              <h2>New Booking</h2>
              <button 
                className="close-btn"
                onClick={() => setShowBookingForm(false)}
              >
                ×
              </button>
            </div>
            <form className="booking-form" onSubmit={handleCreateBooking}>
              <div className="form-group">
                <label>Aircraft:</label>
                <select 
                  value={newBooking.aircraftId}
                  onChange={(e) => setNewBooking({
                    ...newBooking,
                    aircraftId: e.target.value
                  })}
                >
                  <option value="">Select Aircraft</option>
                  {aircraftList.map(aircraft => (
                    <option key={aircraft.id} value={aircraft.id}>
                      {aircraft.type} ({aircraft.registration})
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Instructor:</label>
                <select
                  value={newBooking.staffId}
                  onChange={(e) => setNewBooking({
                    ...newBooking,
                    staffId: e.target.value
                  })}
                >
                  <option value="">Select Instructor</option>
                  {staffList.map(staff => (
                    <option key={staff.id} value={staff.id}>
                      {staff.firstName} {staff.lastName}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Member:</label>
                <select
                  value={newBooking.memberId}
                  onChange={(e) => setNewBooking({
                    ...newBooking,
                    memberId: e.target.value
                  })}
                >
                  <option value="">Select Member</option>
                  {membersList.map(member => (
                    <option key={member.id} value={member.id}>
                      {member.firstName} {member.lastName}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Date:</label>
                <input
                  type="date"
                  value={newBooking.date}
                  onChange={(e) => setNewBooking({
                    ...newBooking,
                    date: e.target.value
                  })}
                  required
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Start Time:</label>
                  <input
                    type="time"
                    value={newBooking.startTime}
                    onChange={(e) => setNewBooking({
                      ...newBooking,
                      startTime: e.target.value
                    })}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>End Time:</label>
                  <input
                    type="time"
                    value={newBooking.endTime}
                    onChange={(e) => setNewBooking({
                      ...newBooking,
                      endTime: e.target.value
                    })}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Comments:</label>
                <textarea
                  value={newBooking.comments}
                  onChange={(e) => setNewBooking({
                    ...newBooking,
                    comments: e.target.value
                  })}
                  rows="3"
                />
              </div>

              <div className="modal-footer">
                <button 
                  type="button" 
                  className="cancel-btn"
                  onClick={() => setShowBookingForm(false)}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="confirm-btn"
                >
                  Create Booking
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Scheduler;
