import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Sidebar.css';
import logo from '../../your-logo.png';

const Sidebar = () => {
  const location = useLocation();
  
  const menuItems = [
    { icon: 'üìÖ', label: 'Bookings', path: '/' },
    { icon: 'üë•', label: 'Staff', path: '/staff' },
    { icon: 'üí∞', label: 'Sales', path: '/sales' },
    { icon: 'üßë‚Äç‚úàÔ∏è', label: 'Members', path: '/members' },
    { icon: 'üìä', label: 'Reports', path: '/reports' },
    { icon: '‚úàÔ∏è', label: 'Aircraft', path: '/aircraft' },
    { icon: '‚öôÔ∏è', label: 'Settings', path: '/settings' },
  ];

  
  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <img src={logo} alt="Aeroclub Logo" className="logo" />
        <h2>Example Aeroclub</h2>
      </div>
      <nav className="sidebar-nav">
        {menuItems.map((item) => (
          <Link
            key={item.label}
            to={item.path}
            className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
          </Link>
        ))}
      </nav>
    </div>
  );
};

export default Sidebar;
