import React, { useState } from 'react';
import Timeline from 'react-calendar-timeline';
import 'react-calendar-timeline/lib/Timeline.css';
import moment from 'moment';
import { useAppContext } from '../../context/AppContext';
import BookingDetailsModal from '../../components/BookingDetailsModal/BookingDetailsModal';

import './Scheduler.css';
import './ConfirmationDialog.css';

const Scheduler = () => {
  const {
    staffList,
    aircraftList,
    bookings,
    setBookings,
    membersList
  } = useAppContext();
  
  const [showConfirmationDialog, setShowConfirmationDialog] = useState(false);
  const [draggedEventInfo, setDraggedEventInfo] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isNewBooking, setIsNewBooking] = useState(false);

  // Format groups (resources) for the timeline
  const groups = [
    // Staff group
    ...staffList.map(staff => ({
      id: staff.id,
      title: `${staff.firstName} ${staff.lastName}`,
      group: 'Staff'
    })),
    // Aircraft group
    ...aircraftList.map(aircraft => ({
      id: aircraft.id,
      title: `${aircraft.type} (${aircraft.registration})`,
      group: 'Aircraft'
    }))
  ];

  // Format items (events) for the timeline
  const items = bookings.map(booking => {
    const member = membersList.find(m => m.id === booking.memberId);
    const memberName = member ? `${member.firstName} ${member.lastName}` : 'Unknown Member';
    return {
      id: booking.id,
      group: booking.aircraftId,
      title: memberName,
      start_time: moment(booking.dateTime).valueOf(),
      end_time: moment(booking.endDateTime).valueOf(),
      canMove: true,
      canResize: true,
      canChangeGroup: true,
      itemProps: {
        style: {
          background: '#3174ad',
          color: 'white',
          borderRadius: '4px',
          padding: '2px 6px',
        }
      }
    };
  });

  // Add these console.logs to debug
  console.log('Groups:', groups);
  console.log('Items:', items);

  return (
    <div className="scheduler-container" style={{ height: '100vh', padding: '20px' }}>
      <div className="scheduler-header">
        <button className="new-booking-btn" onClick={() => setIsNewBooking(true)}>
          New Booking
        </button>
      </div>
      <div style={{ height: 'calc(100vh - 100px)' }}>
        <Timeline
          groups={groups}
          items={items}
          defaultTimeStart={moment().startOf('day').add(6, 'hour')}
          defaultTimeEnd={moment().startOf('day').add(20, 'hour')}
          canMove={true}
          canResize="both"
          onItemMove={(itemId, dragTime, newGroupOrder) => {
            const item = items.find(i => i.id === itemId);
            if (!item) return;
            
            const info = {
              event: {
                id: itemId,
                startStr: moment(dragTime).format(),
                endStr: moment(dragTime + (item.end_time - item.start_time)).format()
              },
              newResource: groups[newGroupOrder]
            };
            setDraggedEventInfo(info);
            setShowConfirmationDialog(true);
          }}
          onItemResize={(itemId, time, edge) => {
            const item = items.find(i => i.id === itemId);
            if (!item) return;

            const info = {
              event: {
                id: itemId,
                startStr: edge === 'left' ? moment(time).format() : moment(item.start_time).format(),
                endStr: edge === 'left' ? moment(item.end_time).format() : moment(time).format()
              }
            };
            setDraggedEventInfo(info);
            setShowConfirmationDialog(true);
          }}
          onItemSelect={(itemId) => {
            const booking = bookings.find(b => b.id === itemId);
            setSelectedBooking(booking);
            setIsNewBooking(false);
            setIsModalOpen(true);
          }}
          timeSteps={{
            second: 0,
            minute: 15,
            hour: 1,
            day: 1,
            month: 1,
            year: 1
          }}
          sidebarWidth={200}
          lineHeight={50}
        />
      </div>

      {/* Confirmation Dialog */}
      {showConfirmationDialog && (
        <div className="confirmation-dialog">
          <div className="confirmation-content">
            <h3>Confirm Changes</h3>
            <p>Are you sure you want to make these changes?</p>
            <div className="confirmation-buttons">
              <button onClick={() => setShowConfirmationDialog(false)}>Cancel</button>
              <button
                onClick={() => {
                  // Handle the confirmation logic here
                  setShowConfirmationDialog(false);
                }}
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Booking Modal */}
      {isModalOpen && (
        <BookingDetailsModal
          booking={selectedBooking}
          isNew={isNewBooking}
          onClose={() => setIsModalOpen(false)}
          onSave={(updatedBooking) => {
            if (isNewBooking) {
              setBookings([...bookings, { ...updatedBooking, id: Date.now() }]);
            } else {
              setBookings(bookings.map(b => 
                b.id === updatedBooking.id ? updatedBooking : b
              ));
            }
            setIsModalOpen(false);
          }}
        />
      )}
    </div>
  );
};

export default Scheduler;
