import React, { useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import resourceTimelinePlugin from '@fullcalendar/resource-timeline';
import interactionPlugin from '@fullcalendar/interaction';
import { useAppContext } from '../../context/AppContext';
import BookingDetailsModal from '../../components/BookingDetailsModal/BookingDetailsModal';
import './Scheduler.css';


const Scheduler = () => {
  const { 
    staffList, 
    aircraftList, 
    bookings,
    setBookings,
    updateBookingStatus,
    membersList
  } = useAppContext();

  const [showBookingForm, setShowBookingForm] = useState(false);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [newBooking, setNewBooking] = useState({
    aircraftId: '',
    staffId: '',
    date: '',
    startTime: '',
    endTime: '',
    comments: ''
  });
  const [showConfirmationDialog, setShowConfirmationDialog] = useState(false);
  const [draggedEventInfo, setDraggedEventInfo] = useState(null);

  // Convert bookings to calendar events format
  const calendarEvents = bookings.flatMap(booking => {
    const member = membersList.find(m => m.id === booking.memberId);
    const memberName = member ? `${member.firstName} ${member.lastName}` : 'Unknown Member';
    
    // Create array of events for this booking
    const events = [
      {
        id: booking.id,
        title: memberName,
        start: booking.dateTime,
        end: booking.endDateTime,
        resourceId: booking.aircraftId, // Show on aircraft resource
        extendedProps: {
          status: booking.status,
          aircraftId: booking.aircraftId,
          staffId: booking.staffId,
          comments: booking.comments
        }
      }
    ];

    // If there's a staff member assigned, add another event for the staff resource
    if (booking.staffId) {
      events.push({
        id: booking.id,
        title: memberName,
        start: booking.dateTime,
        end: booking.endDateTime,
        resourceId: booking.staffId, // Show on staff resource
        extendedProps: {
          status: booking.status,
          aircraftId: booking.aircraftId,
          staffId: booking.staffId,
          comments: booking.comments
        }
      });
    }

    return events;
  });

  const calendarOptions = {
    initialView: 'resourceTimelineWeek',
    plugins: [resourceTimelinePlugin, interactionPlugin],
    editable: true,
    selectable: true,
    resources: [
      {
        id: 'staff',
        title: 'Staff',
        children: staffList.map(staff => ({
          id: staff.id,
          title: `${staff.firstName} ${staff.lastName}`
        }))
      },
      {
        id: 'aircraft',
        title: 'Aircraft',
        children: aircraftList.map(aircraft => ({
          id: aircraft.id,
          title: `${aircraft.type} (${aircraft.registration})`
        }))
      }
    ],
    resourceGroupField: 'title',
    eventClick: (info) => {
      const bookingId = info.event.id;
      const booking = bookings.find(b => b.id === bookingId);
      if (booking) {
        setSelectedBooking(booking);
      }
    },
    select: (selectInfo) => {
      setShowBookingForm(true);
      setNewBooking({
        ...newBooking,
        date: selectInfo.startStr.split('T')[0],
        startTime: selectInfo.startStr.split('T')[1].slice(0, 5),
        endTime: selectInfo.endStr.split('T')[1].slice(0, 5),
        staffId: selectInfo.resource?.id || ''
      });
    },
    eventDrop: (info) => {
      console.log('Event dropped!');
      console.log('Info:', info);
      
      // Store the event info
      setDraggedEventInfo(info);
      setShowConfirmationDialog(true);
    },
  };

  const handleCreateBooking = (e) => {
    e.preventDefault();
    const { date, startTime, endTime, aircraftId, staffId, comments } = newBooking;
    
    const newBookingData = {
      id: `booking${Date.now()}`,
      dateTime: `${date}T${startTime}:00`,  // Add seconds to match format
      endDateTime: `${date}T${endTime}:00`,  // Add seconds to match format
      staffId,
      aircraftId,
      comments,
      status: 'Unconfirmed'
    };

    setBookings([...bookings, newBookingData]);
    setShowBookingForm(false);
    setNewBooking({
      aircraftId: '',
      staffId: '',
      date: '',
      startTime: '',
      endTime: '',
      comments: ''
    });
  };

  console.log('showConfirmationDialog:', showConfirmationDialog);
  console.log('draggedEventInfo:', draggedEventInfo);

  return (
    <div className="main-content">
      <div className="calendar-container">
        <div className="header">
          <h1>Schedule</h1>
          <button 
            className="new-booking-btn"
            onClick={() => setShowBookingForm(true)}
          >
            + New Booking
          </button>
        </div>
        <div className="calendar-wrapper">
          <FullCalendar
            {...calendarOptions}
            events={calendarEvents}
          />
        </div>
      </div>
      
      {showBookingForm && (
        <div className="modal-overlay">
          <div className="booking-form-modal">
            <div className="modal-header">
              <h2>New Booking</h2>
              <button 
                className="close-btn"
                onClick={() => setShowBookingForm(false)}
              >
                Ã—
              </button>
            </div>
            <form onSubmit={handleCreateBooking}>
              <div className="form-group">
                <label>Aircraft:</label>
                <select 
                  value={newBooking.aircraftId}
                  onChange={(e) => setNewBooking({
                    ...newBooking,
                    aircraftId: e.target.value
                  })}
                  required
                >
                  <option value="">Select Aircraft</option>
                  {aircraftList.map(aircraft => (
                    <option key={aircraft.id} value={aircraft.id}>
                      {aircraft.type} ({aircraft.registration})
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Instructor:</label>
                <select
                  value={newBooking.staffId}
                  onChange={(e) => setNewBooking({
                    ...newBooking,
                    staffId: e.target.value
                  })}
                  required
                >
                  <option value="">Select Instructor</option>
                  {staffList.map(staff => (
                    <option key={staff.id} value={staff.id}>
                      {staff.firstName} {staff.lastName}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Date:</label>
                <input
                  type="date"
                  value={newBooking.date}
                  onChange={(e) => setNewBooking({
                    ...newBooking,
                    date: e.target.value
                  })}
                  required
                />
              </div>




              <div className="form-times">
                <div className="form-group">
                  <label>Start Time:</label>
                  <input
                    type="time"
                    value={newBooking.startTime}
                    onChange={(e) => setNewBooking({
                      ...newBooking,
                      startTime: e.target.value
                    })}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>End Time:</label>
                  <input
                    type="time"
                    value={newBooking.endTime}
                    onChange={(e) => setNewBooking({
                      ...newBooking,
                      endTime: e.target.value
                    })}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Comments:</label>
                <textarea
                  value={newBooking.comments}
                  onChange={(e) => setNewBooking({
                    ...newBooking,
                    comments: e.target.value
                  })}
                  rows="3"
                />
              </div>

              <div className="modal-footer">
                <button 
                  type="button" 
                  className="cancel-btn"
                  onClick={() => setShowBookingForm(false)}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="confirm-btn"
                >
                  Create Booking
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {selectedBooking && (
        <BookingDetailsModal
          booking={selectedBooking}
          onClose={() => setSelectedBooking(null)}
          onStatusUpdate={(bookingId, newStatus) => {
            updateBookingStatus(bookingId, newStatus);
            setSelectedBooking(null);
          }}
        />
      )}

      {showConfirmationDialog && (
        <div className="confirmation-dialog">
          <div className="confirmation-content">
            <h3>Confirm Changes</h3>
            <p>Are you sure you want to move this booking?</p>
            <div className="confirmation-buttons">
              <button 
                className="cancel-btn"
                onClick={() => {
                  draggedEventInfo.revert();
                  setShowConfirmationDialog(false);
                  setDraggedEventInfo(null);
                }}
              >
                Cancel
              </button>
              <button 
                className="confirm-btn"
                onClick={() => {
                  const updatedBooking = {
                    ...bookings.find(b => b.id === draggedEventInfo.event.id),
                    dateTime: draggedEventInfo.event.startStr,
                    endDateTime: draggedEventInfo.event.endStr
                  };

                  // If dragged to a new resource, update the appropriate ID
                  if (draggedEventInfo.newResource) {
                    const resourceId = draggedEventInfo.newResource.id;
                    if (resourceId.startsWith('staff')) {
                      updatedBooking.staffId = resourceId;
                    } else {
                      updatedBooking.aircraftId = resourceId;
                    }
                  }
                  
                  setBookings(bookings.map(booking => 
                    booking.id === updatedBooking.id ? updatedBooking : booking
                  ));
                  setShowConfirmationDialog(false);
                  setDraggedEventInfo(null);
                }}
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Scheduler;
