import React, { createContext, useState, useContext } from 'react';

const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [staffList, setStaffList] = useState([
    {
      id: 'staff1',
      firstName: 'Sarah',
      lastName: 'Mitchell',
      email: 'sarah@example.com',
      phone: '0400000001',
      ratings: ['CPL', 'MEA', 'IR'],
      instructorCategory: 'Grade 1',
      active: true
    },
    {
      id: 'staff2',
      firstName: 'James',
      lastName: 'Rodriguez',
      email: 'james@example.com',
      phone: '0400000002',
      ratings: ['CPL', 'IR'],
      instructorCategory: 'Grade 2',
      active: true
    },
    // ... add other staff members
  ]);

  const [aircraftList, setAircraftList] = useState([
    {
      id: 'airplane1',
      registration: 'ZK-ABC',
      type: 'Cessna 172',
      maintenanceStatus: 'Serviceable',
      totalHours: 5000,
    },
    {
      id: 'airplane2',
      registration: 'ZK-XYZ',
      type: 'Piper Cub',
      maintenanceStatus: 'Serviceable',
      totalHours: 3000,
    },
    // ... add other aircraft
  ]);

  const [membersList, setMembersList] = useState([
    {
      id: 'member1',
      firstName: 'John',
      lastName: 'Smith',
      email: 'john.smith@email.com',
      phone: '021123456',
      membershipType: 'Full',
      membershipStatus: 'Active',
      medicalExpiry: '2024-12-31',
      licenseNumber: 'PPL123'
    },
    {
      id: 'member2',
      firstName: 'Jane',
      lastName: 'Doe',
      email: 'jane.doe@email.com',
      phone: '021654321',
      membershipType: 'Student',
      membershipStatus: 'Active',
      medicalExpiry: '2024-06-30',
      licenseNumber: 'SPL456'
    }
  ]);

  const [bookings, setBookings] = useState([
    {
      id: 'booking1',
      dateTime: '2024-01-20T09:00:00',
      endDateTime: '2024-01-20T11:00:00',
      memberId: 'member1',
      staffId: 'staff1',
      aircraftId: 'airplane1',
      comments: 'Initial training flight',
      status: 'Confirmed', // Unconfirmed, Confirmed, Complete, Cancelled
      createdAt: '2024-01-15T08:30:00',
      updatedAt: '2024-01-15T08:30:00'
    }
  ]);

  const createBooking = (bookingData) => {
    const newBooking = {
      id: `booking${Date.now()}`,
      ...bookingData,
      status: 'Unconfirmed',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
    setBookings([...bookings, newBooking]);
    return newBooking;
  };

  const updateBookingStatus = (bookingId, newStatus) => {
    setBookings(bookings.map(booking => {
      if (booking.id === bookingId) {
        return {
          ...booking,
          status: newStatus,
          updatedAt: new Date().toISOString()
        };
      }
      return booking;
    }));
  };

  const getBookingsByMember = (memberId) => {
    return bookings.filter(booking => booking.memberId === memberId);
  };

  const getBookingsByStaff = (staffId) => {
    return bookings.filter(booking => booking.staffId === staffId);
  };

  const getBookingsByAircraft = (aircraftId) => {
    return bookings.filter(booking => booking.aircraftId === aircraftId);
  };

  return (
    <AppContext.Provider value={{
      staffList,
      setStaffList,
      aircraftList,
      setAircraftList,
      membersList,
      setMembersList,
      bookings,
      setBookings,
      createBooking,
      updateBookingStatus,
      getBookingsByMember,
      getBookingsByStaff,
      getBookingsByAircraft
    }}>
      {children}
    </AppContext.Provider>
  );
};

export const useAppContext = () => useContext(AppContext);
