import React, { useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import resourceTimelinePlugin from '@fullcalendar/resource-timeline';
import interactionPlugin from '@fullcalendar/interaction';
import { useAppContext } from '../../context/AppContext';
import BookingDetailsModal from '../../components/BookingDetailsModal/BookingDetailsModal';

import './Scheduler.css';
import './ConfirmationDialog.css';


const Scheduler = () => {
  const {
    staffList,
    aircraftList,
    bookings,
    setBookings,
    membersList
  } = useAppContext();



  
  const [showConfirmationDialog, setShowConfirmationDialog] = useState(false);
  const [draggedEventInfo, setDraggedEventInfo] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isNewBooking, setIsNewBooking] = useState(false);

  // Convert bookings to calendar events format
  const calendarEvents = bookings.flatMap(booking => {
    const member = membersList.find(m => m.id === booking.memberId);
    const memberName = member ? `${member.firstName} ${member.lastName}` : 'Unknown Member';
    return [
      {
        id: booking.id,
        title: memberName,
        start: booking.dateTime,
        end: booking.endDateTime,
        resourceId: booking.aircraftId,
        extendedProps: { ...booking }
      },
      booking.staffId && {
        id: booking.id,
        title: memberName,
        start: booking.dateTime,
        end: booking.endDateTime,
        resourceId: booking.staffId,
        extendedProps: { ...booking }
      }
    ].filter(Boolean);
  });

  const handleEventDrop = (info) => {
    info.revert(); // Prevent immediate update
    setDraggedEventInfo(info);
    setShowConfirmationDialog(true);
  };

  const handleConfirm = () => {
    const { event, newResource } = draggedEventInfo;

    const updatedBooking = {
      ...bookings.find(b => b.id === event.id),
      dateTime: event.startStr,
      endDateTime: event.endStr,
      ...(newResource && {
        [newResource.groupId === 'staff' ? 'staffId' : 'aircraftId']: newResource.id
      })
    };

    setBookings(bookings.map(b => (b.id === updatedBooking.id ? updatedBooking : b)));
    setShowConfirmationDialog(false);
    setDraggedEventInfo(null);
  };

  const handleCancel = () => {
    setShowConfirmationDialog(false);
    setDraggedEventInfo(null);
  };

  const handleEventClick = (info) => {
    setSelectedBooking(bookings.find(b => b.id === info.event.id));
    setIsNewBooking(false);
    setIsModalOpen(true);
  };

  const handleNewBooking = () => {
    setSelectedBooking(null);
    setIsNewBooking(true);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedBooking(null);
    setIsNewBooking(false);
  };

  return (
    <div className="scheduler-container">
      <div className="scheduler-header">
        <button className="new-booking-btn" onClick={handleNewBooking}>
          New Booking
        </button>
      </div>
      <FullCalendar
        plugins={[resourceTimelinePlugin, interactionPlugin]}
        initialView="resourceTimelineWeek"
        editable
        droppable
        resources={[
          { id: 'staff', title: 'Staff', children: staffList.map(s => ({ id: s.id, title: `${s.firstName} ${s.lastName}` })) },
          { id: 'aircraft', title: 'Aircraft', children: aircraftList.map(a => ({ id: a.id, title: `${a.type} (${a.registration})` })) }
        ]}
        events={calendarEvents}
        eventDrop={handleEventDrop}
        eventClick={handleEventClick}
        resourceGroupField="title"
        eventOverlap={false}
        eventConstraint={{ startTime: '06:00', endTime: '20:00' }}
      />
      {showConfirmationDialog && (
        <div className="confirmation-dialog">
          <div className="confirmation-content">
            <h3>Confirm Rescheduling</h3>
            <p>Are you sure you want to reschedule this booking?</p>
            <div className="confirmation-buttons">
              <button className="cancel-btn" onClick={handleCancel}>Cancel</button>
              <button className="confirm-btn" onClick={handleConfirm}>Confirm</button>
            </div>
          </div>
        </div>
      )}
      {isModalOpen && (
        <BookingDetailsModal
          booking={selectedBooking}
          isNew={isNewBooking}
          onClose={handleCloseModal}
          onSave={(updatedBooking) => {
            if (isNewBooking) {
              setBookings([...bookings, { ...updatedBooking, id: Date.now().toString() }]);
            } else {
              setBookings(bookings.map(b => 
                b.id === updatedBooking.id ? updatedBooking : b
              ));
            }
            handleCloseModal();
          }}
        />
      )}
    </div>
  );
};

export default Scheduler;
