import React, { useState } from 'react';
import { useAppContext } from '../../context/AppContext';
import './BookingDetailsModal.css';

const BookingDetailsModal = ({ booking, onClose, onStatusUpdate }) => {
  const { 
    staffList, 
    aircraftList,
    membersList,
    setBookings,
    bookings 
  } = useAppContext();

  const [editedBooking, setEditedBooking] = useState(booking);
  const [isEditing, setIsEditing] = useState(false);

  const handleSave = () => {
    setBookings(bookings.map(b => 
      b.id === editedBooking.id ? editedBooking : b
    ));
    setIsEditing(false);
  };

  const getMemberName = (memberId) => {
    const member = membersList.find(m => m.id === memberId);
    return member ? `${member.firstName} ${member.lastName}` : 'Unknown Member';
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="booking-details-modal" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Booking Details</h2>
          <button className="close-btn" onClick={onClose}>Ã—</button>
        </div>
        
        <div className="modal-content">
          {isEditing ? (
            <form onSubmit={(e) => {
              e.preventDefault();
              handleSave();
            }}>
              <div className="form-group">
                <label>Aircraft:</label>
                <select 
                  value={editedBooking.aircraftId}
                  onChange={(e) => setEditedBooking({
                    ...editedBooking,
                    aircraftId: e.target.value
                  })}
                  required
                >
                  {aircraftList.map(aircraft => (
                    <option key={aircraft.id} value={aircraft.id}>
                      {aircraft.type} ({aircraft.registration})
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Instructor:</label>
                <select
                  value={editedBooking.staffId}
                  onChange={(e) => setEditedBooking({
                    ...editedBooking,
                    staffId: e.target.value
                  })}
                >
                  <option value="">No Instructor</option>
                  {staffList.map(staff => (
                    <option key={staff.id} value={staff.id}>
                      {staff.firstName} {staff.lastName}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Date:</label>
                <input
                  type="date"
                  value={editedBooking.dateTime.split('T')[0]}
                  onChange={(e) => {
                    const [oldDate, time] = editedBooking.dateTime.split('T');
                    setEditedBooking({
                      ...editedBooking,
                      dateTime: `${e.target.value}T${time}`
                    });
                  }}
                  required
                />
              </div>

              <div className="form-times">
                <div className="form-group">
                  <label>Start Time:</label>
                  <input
                    type="time"
                    value={editedBooking.dateTime.split('T')[1].slice(0, 5)}
                    onChange={(e) => {
                      const [date] = editedBooking.dateTime.split('T');
                      setEditedBooking({
                        ...editedBooking,
                        dateTime: `${date}T${e.target.value}:00`
                      });
                    }}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>End Time:</label>
                  <input
                    type="time"
                    value={editedBooking.endDateTime.split('T')[1].slice(0, 5)}
                    onChange={(e) => {
                      const [date] = editedBooking.endDateTime.split('T');
                      setEditedBooking({
                        ...editedBooking,
                        endDateTime: `${date}T${e.target.value}:00`
                      });
                    }}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Comments:</label>
                <textarea
                  value={editedBooking.comments}
                  onChange={(e) => setEditedBooking({
                    ...editedBooking,
                    comments: e.target.value
                  })}
                  rows="3"
                />
              </div>

              <div className="modal-footer">
                <button type="button" onClick={() => setIsEditing(false)}>
                  Cancel
                </button>
                <button type="submit">
                  Save Changes
                </button>
              </div>
            </form>
          ) : (
            <>
              <div className="booking-info">
                <div className="status-badge">{editedBooking.status}</div>
                <p><strong>Date:</strong> {new Date(editedBooking.dateTime).toLocaleDateString()}</p>
                <p><strong>Time:</strong> {editedBooking.dateTime.split('T')[1].slice(0, 5)} - {editedBooking.endDateTime.split('T')[1].slice(0, 5)}</p>
                <p><strong>Aircraft:</strong> {aircraftList.find(a => a.id === editedBooking.aircraftId)?.type}</p>
                {editedBooking.staffId && (
                  <p><strong>Instructor:</strong> {staffList.find(s => s.id === editedBooking.staffId)?.firstName}</p>
                )}
                {editedBooking.comments && (
                  <p><strong>Comments:</strong> {editedBooking.comments}</p>
                )}
              </div>

              <div className="modal-footer">
                <button onClick={() => setIsEditing(true)}>
                  Edit Booking
                </button>
                {editedBooking.status === 'Unconfirmed' && (
                  <>
                    <button onClick={() => onStatusUpdate(editedBooking.id, 'Confirmed')}>
                      Confirm
                    </button>
                    <button onClick={() => onStatusUpdate(editedBooking.id, 'Cancelled')}>
                      Cancel
                    </button>
                  </>
                )}
                {editedBooking.status === 'Confirmed' && (
                  <button onClick={() => onStatusUpdate(editedBooking.id, 'Complete')}>
                    Mark Complete
                  </button>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default BookingDetailsModal;
